import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogTitle from "@mui/material/DialogTitle";
import { styled, alpha } from "@mui/material/styles";
import InputBase from "@mui/material/InputBase";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import HomeWorkIcon from "@mui/icons-material/HomeWork";
import LibraryBooksIcon from "@mui/icons-material/LibraryBooks";
import LocalPharmacyIcon from "@mui/icons-material/LocalPharmacy";
import PropTypes from "prop-types";
import * as React from "react";
import { NavLink } from "react-router-dom";
import image from "../../assets/logo1.png";
import Login from "../../screens/login/Login";
import "./Header.css";
import { IconButton, Tab, Tabs } from "@mui/material";
import EditProfile from "../../screens/register/EditProfile";

export default function Header({ loginHandler }) {

  const [isOpen, setIsOpen] = React.useState(false);
  const [isProfileOpen, setIsProfileOpen] = React.useState(false);
  // const [selectValue, setSelectValue] = React.useState("");
  // const [value, setValue] = React.useState(1);
  // let data = {
  //   label:""
  // }
  const [logButtonName, setlogButtonName] = React.useState(
    isUserSessionAlreadyActive()
  );

  function isUserSessionAlreadyActive() {
    if (
      localStorage.getItem("username") !== "" &&
      localStorage.getItem("username") !== undefined &&
      localStorage.getItem("username") !== null
    ) {
      loginHandler(true);
      return "LOGOUT";
    } else {
      loginHandler(false);
      return "LOGIN";
    }
  }

  function toggleModal() {
    if (logButtonName === "LOGOUT") {
      localStorage.removeItem("firstname");
      localStorage.removeItem("lastname");
      localStorage.removeItem("username");
      localStorage.removeItem("userId");
      localStorage.removeItem("token");
      localStorage.removeItem("role");
      setlogButtonName("LOGIN");
      window.location.replace("/");
    } else {
      setIsOpen(!isOpen);
    }
  }

  function toggleProfileModal() {
    setIsProfileOpen(!isProfileOpen);
  }

  const BootstrapDialog = styled(Dialog)(({ theme }) => ({
    "& .MuiDialogContent-root": {
      padding: theme.spacing(2),
      overflowY: "unset",
    },
    "& .MuiDialogActions-root": {
      padding: theme.spacing(1),
    },
  }));

  const BootstrapProfileDialog = styled(Dialog)(({ theme }) => ({
    "& .MuiDialog-paper": {
      padding: theme.spacing(2),
      minWidth: "900px !important",
      height: "600px",
    },
    "& .MuiDialogActions-root": {
    },
  }));

  // const Search = styled("div")(({ theme }) => ({
  //   position: "relative",
  //   borderRadius: theme.shape.borderRadius,
  //   backgroundColor: alpha(theme.palette.common.white, 0.15),
  //   "&:hover": {
  //     backgroundColor: alpha(theme.palette.common.white, 0.25),
  //   },
  //   marginRight: theme.spacing(2),
  //   marginLeft: 0,
  //   width: "100%",
  //   [theme.breakpoints.up("sm")]: {
  //     marginLeft: theme.spacing(3),
  //     width: "auto",
  //   },
  // }));

  // const SearchIconWrapper = styled("div")(({ theme }) => ({
  //   padding: theme.spacing(0, 2),
  //   height: "100%",
  //   position: "absolute",
  //   pointerEvents: "none",
  //   display: "flex",
  //   alignItems: "center",
  //   justifyContent: "center",
  // }));
  // const StyledInputBase = styled(InputBase)(({ theme }) => ({
  //   color: "inherit",
  //   "& .MuiInputBase-input": {
  //     border: "2px SOLID red",
  //     padding: theme.spacing(1, 1, 1, 0),
  //     // vertical padding + font size from searchIcon
  //     paddingLeft: `calc(1em + ${theme.spacing(4)})`,
  //     transition: theme.transitions.create("width"),
  //     width: "100%",
  //     [theme.breakpoints.up("md")]: {
  //       width: "20ch",
  //     },
  //   },
  // }));
  const BootstrapDialogTitle = (props) => {
    const { children, onClose, ...other } = props;
    return (
      <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
        {children}
        {onClose ? (
          <IconButton
            aria-label="close"
            onClick={onClose}
            sx={{
              position: "absolute",
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          />
        ) : null}
      </DialogTitle>
    );
  };

  BootstrapDialogTitle.propTypes = {
    children: PropTypes.node,
    onClose: PropTypes.func.isRequired,
  };
  return (
    <Box sx={{ flexGrow: 1, display: "flex" }}>
      <AppBar position="fixed">
        <Toolbar
          className="toolbar"
          position="fixed"
          style={{
            backgroundColor: "#262673",
            height: "75px",
            width: "100%",
            position: "fixed",
            borderBottom: "0.1em solid #B1F4F1",
            padding: "0.5em",
          }}
        >
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 1 }}
          >
            <img src={image} className="img" style={{ height: "60px" }} />
          </IconButton>
          <Typography
            variant="h4"
            component="div"
            style={{ color: "#ffffff", fontFamily: "monospace" }}
          >
            BANDIFY
          </Typography>
          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;
          &nbsp;&nbsp;&nbsp;
          <NavLink
            className="navbar-item"
            to="/"
            style={{ color: "#ffffff", textDecoration: "none" }}
          >
            <IconButton>
              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                  flexWrap: "wrap",
                  fontSize: "19px",
                  color: "#ffffff",
                }}
              >
                <HomeWorkIcon />
                &nbsp;<span>HOME</span>
              </div>
            </IconButton>
          </NavLink>
          {localStorage.getItem("username") !== undefined &&
          localStorage.getItem("username") !== null &&
          localStorage.getItem("username").trim() !== "" &&
          localStorage.getItem("role").trim() === "musician" ? (
            <>
              &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;
              <NavLink
                className="navbar-item"
                to="/bands"
                style={{ color: "#ffffff", textDecoration: "none" }}
              >
                <IconButton>
                  &nbsp;&nbsp;
                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      flexWrap: "wrap",
                      fontSize: "19px",
                      color: "#ffffff",
                    }}
                  >
                    <LibraryBooksIcon />
                    &nbsp;<span>MY BAND</span>
                  </div>
                </IconButton>
              </NavLink>
            </>
          ) : (
            ""
          )}
          {localStorage.getItem("username") !== undefined &&
          localStorage.getItem("username") !== null &&
          localStorage.getItem("username").trim() !== "" &&
          localStorage.getItem("role").trim() === "musician" ? (
            <>
              {" "}
              &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;
              <NavLink
                className="navbar-item"
                to="/applications"
                style={{ color: "#ffffff", textDecoration: "none" }}
              >
                <IconButton>
                  &nbsp;&nbsp;
                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      flexWrap: "wrap",
                      fontSize: "19px",
                      color: "#ffffff",
                    }}
                  >
                    <LocalPharmacyIcon />
                    &nbsp;<span>MY APPLICATION</span>
                  </div>
                </IconButton>
              </NavLink>
            </>
          ) : (
            ""
          )}
          <div style={{ flex: "1" }} />
          &nbsp;&nbsp;&nbsp;
          {localStorage.getItem("username") !== undefined &&
          localStorage.getItem("username") !== null &&
          localStorage.getItem("username").trim() !== "" ? (
            <div
              onClick={toggleProfileModal}
              style={{ cursor: "pointer", color: "#ffffff", fontSize: "19px" }}
            >
              <span>
                {" "}
                Hello {localStorage.getItem("firstname")}&nbsp;
                {localStorage.getItem("lastname")}!
              </span>
              &nbsp;&nbsp;
            </div>
          ) : (
            ""
          )}
          <Button
            variant="contained"
            style={{ backgroundColor: "goldenrod", color: "white" }}
            onClick={toggleModal}
          >
            {logButtonName}
          </Button>
          <BootstrapDialog
            onClose={toggleModal}
            aria-labelledby="customized-dialog-title"
            open={isOpen}
          >
            <BootstrapDialogTitle
              id="customized-dialog-title"
              onClose={toggleModal}
              className="toolHeader"
              style={{
                textAlign: "center",
                backgroundColor: "#262673",
                color: "white",
              }}
            >
              LOGIN
            </BootstrapDialogTitle>

            <Login toggleModal={toggleModal} loginButton={setlogButtonName} />
          </BootstrapDialog>
          <BootstrapProfileDialog
            onClose={toggleProfileModal}
            aria-labelledby="customized-dialog-title"
            open={isProfileOpen}
          >
            <BootstrapDialogTitle
              id="customized-dialog-title"
