import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import { styled, alpha } from '@mui/material/styles';
import InputBase from '@mui/material/InputBase';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import HomeWorkIcon from '@mui/icons-material/HomeWork';
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import LocalPharmacyIcon from '@mui/icons-material/LocalPharmacy';
import TheatersIcon from '@mui/icons-material/Theaters';
import SupervisorAccountIcon from '@mui/icons-material/SupervisorAccount';
import TextField from '@mui/material/TextField';
import Autocomplete from '@mui/material/Autocomplete';
import Popper from '@mui/material/Popper';
import AccountCircleIcon from '@mui/icons-material/AccountBox';
import HomeIcon from '@mui/icons-material/Home';
import SearchIcon from '@mui/icons-material/Search';
import MobileScreenShareIcon from '@mui/icons-material/MobileScreenShare';
import ReceiptLongIcon from '@mui/icons-material/ReceiptRounded';
import ShareIcon from '@mui/icons-material/Share';
import MovieIcon from '@mui/icons-material/Movie';
import HeadsetMicIcon from '@mui/icons-material/HeadsetMic';
import BookmarksIcon from '@mui/icons-material/Bookmarks';
import AddAPhotoIcon from '@mui/icons-material/AddAPhoto';
import PhotoLibraryIcon from '@mui/icons-material/PhotoLibrary';
import PropTypes from 'prop-types';
import * as React from 'react';
import { getAllPosts, logoutUser} from '../../util/apiCalls';
import {
  NavLink
} from 'react-router-dom';
import image from '../../assets/logo.png';
import Login from '../../screens/login/Login';
import "./Header.css";
import ViewProfile from '../../screens/profile/ViewProfile';
import { IconButton, Tab, Tabs } from '@mui/material';
import { TabPanel, TabContext  } from '@mui/lab';
import Profile from '../../screens/profile/Profile';
import EditProfile from '../../screens/register/EditProfile';

export default function Header({ loginHandler }) {

  //This js file is to design & api calls related to header section in ui screen

  const [isOpen, setIsOpen] = React.useState(false);
  const [isProfileOpen, setIsProfileOpen] = React.useState(false);
  const [selectValue, setSelectValue] = React.useState("");
  const [value, setValue] = React.useState(1);
  let datas = {
    label:""
  }
  const [logButtonName, setlogButtonName] = React.useState(isUserSessionAlreadyExist());


  //This function is to validate user session exists or not
  function isUserSessionAlreadyExist() {
    if (localStorage.getItem("username") !== "" && localStorage.getItem("username") !== undefined
      && localStorage.getItem("username") !== null) {
      loginHandler(true);
      return "LOGOUT";
    } else {
      loginHandler(false);
      return "LOGIN";
    }
  }

  function toggleModal() {
    //alert(selectValue);
    if (logButtonName === 'LOGOUT') {
      // logoutUser(localStorage.getItem("userId")).then(resp => {
      //   console.log(resp);
      //   resp.json().then(data => {
      //     console.log(data);
      //   });
      // });
      localStorage.removeItem("firstname");
      localStorage.removeItem("lastname");
      localStorage.removeItem("username");
      localStorage.removeItem("userId");
      localStorage.removeItem("token");
      localStorage.removeItem("role");
      setlogButtonName("LOGIN");
      window.location.replace("/")
    } else {
      setIsOpen(!isOpen);
    }
  }

  function toggleProfileModal() {
    setIsProfileOpen(!isProfileOpen);
  }

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };



  

  //alert(selectValue);
  const BootstrapDialog = styled(Dialog)(({ theme }) => ({
    '& .MuiDialogContent-root': {
      padding: theme.spacing(2),
     //width: '300px !important',
     overflowY: 'unset'
    },
    '& .MuiDialogActions-root': {
      padding: theme.spacing(1),
    },
  }));

  const BootstrapProfileDialog = styled(Dialog)(({ theme }) => ({
    '& .MuiDialog-paper': {
      padding: theme.spacing(2),
      minWidth: '900px !important',
      height: '600px'
    },
    '& .MuiDialogActions-root': {
     // padding: theme.spacing(1),
     // minWidth: '700px'
    },
  }));

   const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: alpha(theme.palette.common.white, 0.15),
    '&:hover': {
      backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    marginRight: theme.spacing(2),
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing(3),
      width: 'auto',
    },
  }));
  
  const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  }));
  const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: 'inherit',
    '& .MuiInputBase-input': {
      border: '2px SOLID red',
      padding: theme.spacing(1, 1, 1, 0),
      // vertical padding + font size from searchIcon
      paddingLeft: `calc(1em + ${theme.spacing(4)})`,
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('md')]: {
        width: '20ch',
      },
    },
  }));
  const BootstrapDialogTitle = (props) => {
    const { children, onClose, ...other } = props;
    return (
      <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
        {children}
        {onClose ? (
          <IconButton
            aria-label="close"
            onClick={onClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
          </IconButton>
        ) : null}
      </DialogTitle>
    );
  };

  BootstrapDialogTitle.propTypes = {
    children: PropTypes.node,
    onClose: PropTypes.func.isRequired,
  };
  return (
    <Box sx={{ flexGrow: 1, display: "flex" }}>
      <AppBar position="fixed"  >
        <Toolbar className="toolBar" position="fixed" style={{ backgroundColor: '#262673', height: '75px', width: '100%', position: 'fixed', borderBottom: '0.1em solid #B1F4F1', padding: '0.5em' }}>
          <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 1 }}
          >
            <img src={image} className="img" style={{ height: '60px' }} />
          </IconButton>
          <Typography variant="h4" component="div" style={{ color: '#ffffff', fontFamily: 'monospace' }} >
            BANDIFY
          </Typography>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;
          
          {/* <NavLink className="navbar-item" to="/home" style={{ color: '#373C83', textDecoration: 'none' }}> */}
          {/* <Search style={{backgroundColor:'white', borderRadius:'20px'}}>
            <SearchIconWrapper>
              <SearchIcon />
            </SearchIconWrapper>
            <Autocomplete
              disablePortal
              id="combo-box-demo"
              options={posts}
              defaultValue={selectValue}
             // getOptionLabel={(option) => option.label}
              onChange={(event, value) => setSelectValue(value.label)}
              sx={{ width: 700 }}
              PopperComponent={({ style, ...props }) => (
                <Popper
                  {...props}
                  style={{ ...style, height: 0 , color:'white', borderRadius:'20px'}} // width is passed in 'style' prop
                />
