import LoginIcon from '@mui/icons-material/Login';
import PlaylistAddCheckCircleIcon from '@mui/icons-material/PlaylistAddCheckCircle';
import { Box, CardHeader, Chip, Collapse, FormControl, Grid, InputLabel, MenuItem, Paper, Select, Snackbar, TextField } from '@mui/material';
import Button from '@mui/material/Button';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import VolumeUpIcon from '@mui/icons-material/VolumeUp';
import IconButton from '@mui/material/IconButton';
import SpatialAudioOffIcon from '@mui/icons-material/SpatialAudioOff';
import MenuIcon from '@mui/icons-material/Menu';
import Rating from '@mui/lab/Rating';
import Typography from '@mui/material/Typography';
import PropTypes from 'prop-types';
import * as React from 'react';
import add from '../../assets/add.png';
import deletes from '../../assets/delete.png';
import band from '../../assets/band.png';
import edit from '../../assets/edit.png';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import { styled } from '@mui/material/styles';
import PersonAddAlt1Icon from '@mui/icons-material/PersonAddAlt1';
import PersonRounded from '@mui/icons-material/PersonRounded';
import CardMembershipIcon from '@mui/icons-material/CardMembership';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import SearchIcon from "@mui/icons-material/Search";
import InputAdornment from "@mui/material/InputAdornment";
import { Image, Stack } from 'react-bootstrap';
import Login from '../login/Login';
import Header from '../../common/header/Header';
import moment from 'moment/moment';
import AddBand from './addBand';
import { deleteBandById, getAllApplication, getMyBand, updateApplicationStatus } from '../../util/apiCalls';
import EditBand from './editBand';






const ExpandMore = styled((props) => {
  const { expand, ...other } = props;
  return <IconButton {...other} />;
})(({ theme, expand }) => ({
  transform: !expand ? 'rotate(180deg)' : 'rotate(0deg)',
  marginLeft: 'auto',
  transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
  }),
}));
export default function Band() {
  // const [searchBand, setSearchBand] = React.useState('');
  // const [searchLevel, setSearchLevel] = React.useState('ALL');
  // const [searchRating, setSearchRating] = React.useState('');
  const [eStreet, seteStreet] = React.useState('');
  const [eCity, seteCity] = React.useState('');
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);
  const [openSnack, setOpenSnack] = React.useState(false);
  const [snackMessage, setSnackMessage] = React.useState('');
  const [eState, seteState] = React.useState('');
  const [isEditBandOpen, setIsEditBandOpen] = React.useState(false);
  const [isAddBandsOpen, setIsAddBandsOpen] = React.useState(false);
  // const [isViewLessonOpen, setIsViewLessonOpen] = React.useState(false);
  // const [isViewQuizOpen, setIsViewQuizOpen] = React.useState(false);
  const [value, setValue] = React.useState(1);
  const [bandData, setBandData] = React.useState("");
  const [applications, setApplications] = React.useState([]);
  // const [isOpen, setIsOpen] = React.useState(false);
  // const [logButtonName, setlogButtonName] = React.useState("LOGIN");
  const [eselectValue, seteSelectValue] = React.useState([]);
  const [selectedBandId, setSelectedBandId] = React.useState('');
  const [expanded, setExpanded] = React.useState(-1);

  const loginHandler = (value) => {
    setIsLoggedIn(value);
  }
  React.useEffect(() => {
    getLoggedInStatus();

  }, [value]);

const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  React.useEffect(() => {
      getMyBand().then(resp => {
        console.log(resp);
        resp.json().then(data => {
            console.log(data);
            setBandData(data);
            if(data.id !== undefined){
              getAllApplication(data.id).then(resp => {
                console.log(resp);
                resp.json().then(data => {
                    console.log(data);
                    setApplications(data);
        
                  });
                
              }).catch(error => {
                console.log("login user err " + error);
              });
            }
           

          });
        
      }).catch(error => {
        console.log("login user err " + error);
      });
  }, [isLoggedIn])


  const handleSnackClose = () => {
    setOpenSnack(!openSnack);
  };

  
  function getLoggedInStatus() {
    if (localStorage.getItem("username") !== "" && localStorage.getItem("username") !== undefined
      && localStorage.getItem("username") !== null) {
      console.log("Logged in already");
      setIsLoggedIn(true);
    } else {
      console.log("LOgged in not yet");
      setIsLoggedIn(false);
      window.location.replace("/")
    }
  }

  const DialogAddSlot = styled(Dialog)(({ theme }) => ({
    '& .MuiDialog-paper': {
      padding: theme.spacing(2),
      minWidth: '1000px !important',
      height: '500px'
    },
    '& .MuiDialogActions-root': {
      padding: theme.spacing(1),
    },
  }));

  const DialogViewLesson = styled(Dialog)(({ theme }) => ({
    '& .MuiDialog-paper': {
      padding: theme.spacing(2),
      minWidth: '1000px !important',
      height: '700px'
    },
    '& .MuiDialogActions-root': {
      padding: theme.spacing(1),
    },
  }));

  const DialogLogin = styled(Dialog)(({ theme }) => ({
    '& .MuiDialog-paper': {
      minWidth: '600px !important',
      height: '300px',
      overflowY: 'none'
    },
    '& .MuiDialogActions-root': {
      padding: theme.spacing(1),
      overflowY: 'none'
    },
  }));
  const DialogForViewBandDetails = styled(Dialog)(({ theme }) => ({
    '& .MuiDialog-paper': {
      padding: theme.spacing(2),
      minWidth: '800px !important',
      height: '500px'
    },
    '& .MuiDialogActions-root': {
      padding: theme.spacing(1),
    },
  }));
  const DialogTitleForModal = (props) => {
    const { children, onClose, ...other } = props;
    return (
      <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
        {children}
        {onClose ? (
          <IconButton
            aria-label="close"
            onClick={onClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
          </IconButton>
        ) : null}
      </DialogTitle>
    );
  };


  DialogTitleForModal.propTypes = {
    children: PropTypes.node,
    onClose: PropTypes.func.isRequired,
  };



  const handleChange = (event) => {
    const {
      target: { value },
    } = event;
    seteSelectValue(typeof value === 'string' ? value.split(',') : value);
  };
  const ITEM_HEIGHT = 48;
  const ITEM_PADDING_TOP = 8;
  const MenuProps = {
    PaperProps: {
      style: {
        maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
        width: 250,
      },
    },
  };

  //This function is to validate user session exists or not
  function isUserSessionAlreadyExist() {
    if (localStorage.getItem("username") !== "" && localStorage.getItem("username") !== undefined
      && localStorage.getItem("username") !== null) {
      loginHandler(true);
      return "LOGOUT";
    } else {
      loginHandler(false);
      return "LOGIN";
    }
  }

  
  function toggleAddBandModal() {
    setIsAddBandsOpen(!isAddBandsOpen);
    if (isAddBandsOpen === true) {
      getMyBand().then(resp => {
        console.log(resp);
        resp.json().then(data => {
            console.log(data);
            setBandData(data);
          });
        
      }).catch(error => {
        console.log("login user err " + error);
      });
    }
  }

  function toggleDeleteBandModal(bandId) {
     deleteBandById(bandId).then(resp => {
      getMyBand().then(resp => {
        console.log(resp);
        resp.json().then(data => {
            console.log(data);
            setBandData(data);
          });
        
      }).catch(error => {
        console.log("login user err " + error);
      });
    }).catch(error => {
      console.log("deleteBandById user err " ,error);
      setSnackMessage(error.response.data.message);
      setOpenSnack(true);
    });
  }

 

  const toggleEditBandModal = (bandId) => {
    setIsEditBandOpen(!isEditBandOpen);
    setSelectedBandId(bandId);
    if (isEditBandOpen === true) {
      getMyBand().then(resp => {
        console.log(resp);
        resp.json().then(data => {
            console.log(data);
            setBandData(data);
          });
        
      }).catch(error => {
        console.log("login user err " + error);
      });
    }
  }

  function toggleApplicationStatusBandModal(bandId, musicianId, status){
    updateApplicationStatus(bandId, musicianId, status).then(resp => {
      console.log(resp);
      resp.json().then(data => {
        getMyBand().then(resp => {
          console.log(resp);
          resp.json().then(data => {
              console.log(data);
              setBandData(data);
              if(data.id !== undefined){
                getAllApplication(data.id).then(resp => {
                  console.log(resp);
                  resp.json().then(data => {
                      console.log(data);
                      setApplications(data);
          
                    });
                  
                }).catch(error => {
                  console.log("login user err " + error);
                });
              }
            });
          
        }).catch(error => {
          console.log("login user err " + error);
        });
        });
      
    }).catch(error => {
      console.log("login user err " + error);
    });

  }
  
  return (
    <React.Fragment>
      <Header loginHandler={loginHandler} />
      <br></br><br></br><br></br><br></br><br></br>

      {(localStorage.getItem("username") !== undefined && localStorage.getItem("username") !== null && localStorage.getItem("username").trim() !== "" ? (
        <>
          

          {(localStorage.getItem("username") !== undefined && localStorage.getItem("username") !== null && localStorage.getItem("username").trim() !== "" && localStorage.getItem("role").trim() === "musician") ? (
           <Image src={add} onClick={toggleAddBandModal} style={{ float: 'right', height: '60px', marginRight: '3%', marginTop: '-10px', cursor: 'pointer' }}></Image>
          ) : ""}
          <br></br><br></br>
          <Grid container direction="row" rowSpacing={2} style={{ paddingLeft: '100px' }} spacing={{ xs: 2, md: 4 }} columns={{ xs: 4, sm: 8, md: 12 }}>




            { bandData && bandData !== "" && bandData !== undefined && bandData.bandName !== null ? (
               <Grid item>
               <Card sx={{   display:'flex', minWidth:'87%', maxWidth:'87%' }} >
                 <CardMedia
                 sx={{ padding: "1em 1em 0 1em", objectFit: "contain", minWidth:'15%', maxWidth:'15%'  }}
                 
                   component="img"
                   image={band}
                   
                 />
                 <Box sx={{ display: 'inline-flex', flexDirection: 'column', width:"90%" }}>
                 <CardActions>
                 <CardContent style={{ fontFamily: 'Roboto', width:"100%"}}>
                 <div style={{display:'flex', flexWrap:'wrap'}}>
                   <Grid item md={10}>
                   <Typography gutterBottom variant="h6" component="div" style={{  fontSize: 17, fontWeight: '700', float:'left', fontFamily:'fantasy' }}>
                     {bandData.bandName}
                   </Typography>
                   </Grid>
                   <Grid item md={2}>
                   <Typography variant="body2" color="text.secondary" style={{display: 'inline-flex', float:'right'}}>
                     <Chip style={{float:'right'}} label={"Price : "+ (bandData.price) +" "+ (bandData.currency.code)} color='success'>
                      
                     </Chip>
                     </Typography>
                     </Grid>
                   </div>
                   
                   
                   <Typography variant="body2" color="text.secondary" >
                     Genre : {bandData.genre.genre}
                   </Typography>
                   <br></br>
                   <div style={{display:'flex', flexWrap:'wrap', float:"left"}}>
                   
                   <PersonRounded/>{bandData.createdBy.firstName} {bandData.createdBy.lastName} ({bandData.createdBy.username})
                   
                   </div>

                   <div style={{display:'flex', flexWrap:'wrap', float:"right"}}>
                    <Button variant='contained' onClick={(e) => toggleEditBandModal(bandData.id)}> 
                    <Image src={edit} size="small"  style={{ float: 'right', height: '20px', cursor: 'pointer' }}></Image>
                    &nbsp;EDIT</Button>&nbsp;
                    <Button variant='contained' onClick={(e) => toggleDeleteBandModal(bandData.id)} >  
                    <Image src={deletes} style={{ float: 'right', height: '20px', cursor: 'pointer' }}></Image>
                    &nbsp;DELETE</Button>
                    </div>
                   
                   <br></br>
                   </CardContent>
                   <ExpandMore
                       expand={expanded}
                       onClick={()=>handleExpandClick()}
                       aria-expanded={expanded}
                       aria-label="show more"
                       >
                           <IconButton  style={{backgroundColor:  "#001f3d"}}>
                       <ExpandMoreIcon style={{fill: "white"}}/>
                       </IconButton>
                       </ExpandMore>
                       </CardActions>
                       <Collapse in={expanded} timeout="auto" unmountOnExit >
                       <Grid container direction={'row'}>
                         <Grid item md={5}>
                         <Card style={{padding:'10px'}}>
                         <CardHeader avatar={<VolumeUpIcon style={{fill:'white'}}/>} title={"Instruments"} titleTypographyProps={{variant:'body1' }} style={{backgroundColor:'#262673', color:'white'}}>
                           </CardHeader>
                           <CardContent>
                           {
                             bandData.instruments.length > 0 ? (
                               <Box>
                                 {bandData.instruments.map((ins, ind) => (
                                    
                                   <Typography variant="body2" color="text.secondary" >
                                       {ins.name}
                                   </Typography>

                                     
                                 ))}
                                 </Box>
                              ) : ""
                           }
                           </CardContent>
                         </Card>
                         </Grid>
                         <Grid item md={1}></Grid>
                         <Grid item md={5}>
                         <Card style={{padding:'10px'}}>
                           <CardHeader avatar={<SpatialAudioOffIcon style={{fill:'white'}}/>} title={"Musicians"} titleTypographyProps={{variant:'body1' }} style={{backgroundColor:'#262673', color:'white'}}>
                           </CardHeader>
                           <CardContent>
                           {
                             bandData.musicians.length > 0 ? (
                               <Box>
                                 {bandData.musicians.map((ins, ind) => (
                                    <>
                                    <div style={{display:'flex', flexWrap:'wrap', padding:'5px'}}>
                   
                                    <PersonRounded/>{ins.firstName} {ins.lastName} 
                                    
                                    </div>
                                    </>
                                 ))}
                                 </Box>
                              ) : ""
                           }
                           </CardContent>
                           </Card>
                           </Grid>
                           </Grid>
                           <br></br><br></br>
                           <Grid item md={11}>
                           <Card style={{padding:'10px'}}>
                           <CardHeader avatar={<MenuIcon style={{fill:'white'}}/>} title={"APPLICATIONS"} titleTypographyProps={{variant:'body1' }} style={{backgroundColor:'#262673', color:'white'}}>
                           </CardHeader>
                           <CardContent>
                          {
                            applications.length > 0 ? (
                              <Box>
                                {applications.map((ins, ind) => (
                                  <>
                                   <Card style={{padding:'10px', backgroundColor:'#b3f3f5'}}>
                                   <div style={{display:'flex', flexWrap:'wrap', float:'left'}}>
                  
                                   <PersonRounded/>{ins.musician.firstName} {ins.musician.lastName}
                                   
                                   </div>
                                   <br></br><br></br>
                                    <Typography variant='p' >INSTRUMENTS</Typography>
                                   {ins.musician.instruments.map((eaIns, eaInd) => (
                                      <Typography variant='subtitle2'>{eaIns.name}</Typography>
                                   ))}
                                   
                                  <div style={{float:'right'}}>
                                   <Button variant='contained' onClick={(e) => toggleApplicationStatusBandModal(ins.band.id, ins.musician.id, "approved")} > 
                                   
                                   APPROVE
                                   </Button> &nbsp;&nbsp;
                                   <Button variant='contained' onClick={(e) => toggleApplicationStatusBandModal(ins.band.id, ins.musician.id, "declined")} > 
                                  
                                   DECLINE
                                   </Button>
                                   </div>
                                  
                                   </Card><br></br><br></br>
                                    </>
                                ))}
                                </Box>
                             ) : (
                              <p style={{color:'red', padding:'10px'}}>No records found!</p>
                             )
                          }
                          </CardContent>
                           </Card>
                           </Grid>
                           <br></br><br></br>
                   </Collapse>
                   </Box>
                   </Card>
                   </Grid>
            ):(
              <p style={{color:'red'}}>No records found!</p>
            )
                  }


          </Grid>
          <br></br><br></br><br></br>
        </>
      ) : (
        
        <></>
      )
      )}

<DialogAddSlot
        onClose={toggleAddBandModal}
        aria-labelledby="customized-dialog-title"
        open={isAddBandsOpen}
      >
        <DialogTitleForModal id="customized-dialog-title" className="toolHeader" style={{ textAlign: 'center', color: 'white' }}>
          ADD NEW BAND
        </DialogTitleForModal>

        <AddBand addBandModal={toggleAddBandModal} />

      </DialogAddSlot>

      <DialogAddSlot
        onClose={toggleEditBandModal}
        aria-labelledby="customized-dialog-title"
        open={isEditBandOpen}
      >
        <DialogTitleForModal id="customized-dialog-title" className="toolHeader" style={{ textAlign: 'center', color: 'black' }}>
         EDIT Band
        </DialogTitleForModal>

        <EditBand editBandModal={toggleEditBandModal} bandId={selectedBandId}/>

      </DialogAddSlot>



      <Snackbar
        style={{ top: '50%', bottom: '50%', left: '40%', right: '50%' ,whiteSpace: 'pre-wrap', width: '300px'}}
        autoHideDuration={4000}
        anchorOrigin={{
          vertical: "center",
          horizontal: "center"
        }}
        open={openSnack}
        onClose={handleSnackClose}
        message={snackMessage}
      />
    </React.Fragment>
  );
}
