import { Grid, Avatar, Snackbar, Box, Collapse, Chip, Paper, CardHeader, Alert } from '@mui/material';
import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Header from '../../common/header/Header';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import BookmarksIcon from '@mui/icons-material/Bookmarks';
import band from '../../assets/band.png';
import PersonRounded from '@mui/icons-material/PersonRounded';
import VolumeUpIcon from '@mui/icons-material/VolumeUp';
import IconButton from '@mui/material/IconButton';
import SpatialAudioOffIcon from '@mui/icons-material/SpatialAudioOff';
import { getAllApplicationStatus, getAllBands, getMyBand, saveApplication } from '../../util/apiCalls';
import PropTypes from 'prop-types';
import { styled } from '@mui/material/styles';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore'

const ExpandMore = styled((props) => {
  const { expand, ...other } = props;
  return <IconButton {...other} />;
})(({ theme, expand }) => ({
  transform: !expand ? 'rotate(180deg)' : 'rotate(0deg)',
  marginLeft: 'auto',
  transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
  }),
}));
export default function Home() {

  const [value, setValue] = React.useState(1);
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);
  const [openSnack, setOpenSnack] = React.useState(false);
  const [snackMessage, setSnackMessage] = React.useState('');
  const [bands, setBands] = React.useState([]);
  const [bandOwner, setBandOwner] = React.useState(false);
  const [application, setApplication] = React.useState([]);
  const [appliedBandIds, setAppliedBandIds] = React.useState([]);
  const [isOpen, setIsOpen] = React.useState(false);
  const [isViewMovieOpen, setIsViewMovieOpen] = React.useState(false);
  const [severity, setSeverity] = React.useState('success');
  const [expanded, setExpanded] = React.useState(-1);
  const loginHandler = (value) => {
    setIsLoggedIn(value);
  }
  React.useEffect(() => {
    getLoggedInStatus();
    
    getAllBand();
    getAllApplicationStatusByMusician();
  }, [isLoggedIn]);

  React.useEffect(() => {
    getMyBand().then(resp => {
      console.log(resp);
      resp.json().then(data => {
        if(data.bandName !== null){
          setBandOwner(true);
        }
      })
    });
  }, []);

  const handleSnackClose = () => {
    setOpenSnack(!openSnack);
  };

  const handleExpandClick = (i, courseId) => {
    setExpanded(expanded === i ? -1 : i);
  };
  
  // function yourfunc(id) {
  //   if (localStorage.getItem('username') === "" || localStorage.getItem('username') === null) {
  //     setSnackMessage("Please login, before booking");
  //     setOpenSnack(!openSnack);
  //     return false;
  //   }
  //   //console.log(id);
  //
  // }

  function getAllBand(){
    getAllBands().then(resp => {
      console.log(resp);
      resp.json().then(data => {
        setBands(data);
      });
    }).catch(error => {
      console.log("login user err " + error);
    })
  }

  function getAllApplicationStatusByMusician(){
    
    getAllApplicationStatus().then(resp => {
      console.log(resp);
      resp.json().then(data => {

        let arr = [];
        if(localStorage.getItem("username") !== undefined && localStorage.getItem("username") !== null && localStorage.getItem("username").trim() !== "" && localStorage.getItem("role")==="musician"){
          data.map(e => {
            arr.push(e.band.id);
          })
          setAppliedBandIds(arr);
        }
          
        
        setApplication(data);
      });
    }).catch(error => {
      console.log("login user err " + error);
    })
  }

  function openBandsPage(){
    if(isLoggedIn){
      window.location.replace("/");
     
    } else {
      setSeverity("error");
      setSnackMessage('Please login to proceed');
      setOpenSnack(true);
    }

  }

  const toggleApplyBandModal = (bandId) => {
    // alert(bandId);
    saveApplication(bandId).then(resp => {
      // console.log(resp);
      getAllApplicationStatus().then(resp => {
        // console.log(resp);
        resp.json().then(data => {
  
          let arr = [];
  
          data.map(e => {
            arr.push(e.band.id);
          })
          setAppliedBandIds(arr);
          setApplication(data);
        });
      }).catch(error => {
        console.log("login user err " + error);
      })
    }).catch(error => {
      console.log("login user err " + error);
    })
  }

  function getLoggedInStatus() {
    if (localStorage.getItem("username") !== "" && localStorage.getItem("username") !== undefined
      && localStorage.getItem("username") !== null) {
      setIsLoggedIn(true);
       
    } else {
      setIsLoggedIn(false);
    }
  }
  const BootstrapDialog = styled(Dialog)(({ theme }) => ({
    '& .MuiDialog-paper': {
      padding: theme.spacing(2),
      minWidth: '1000px !important',
      height: '800px'
    },
    '& .MuiDialogActions-root': {
      padding: theme.spacing(1),
    },
  }));
  const BootstrapDialogForViewMovie = styled(Dialog)(({ theme }) => ({
    '& .MuiDialog-paper': {
      padding: theme.spacing(2),
      minWidth: '1200px !important',
      height: '900px'
    },
    '& .MuiDialogActions-root': {
      padding: theme.spacing(1),
    },
  }));
  const BootstrapDialogTitle = (props) => {
    const { children, onClose, ...other } = props;
    return (
      <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
        {children}
        {onClose ? (
          <IconButton
            aria-label="close"
            onClick={onClose}
            sx={{
              position: 'absolute',
              right: 8,
              top: 8,
              color: (theme) => theme.palette.grey[500],
            }}
          >
          </IconButton>
        ) : null}
      </DialogTitle>
    );
  };


  BootstrapDialogTitle.propTypes = {
    children: PropTypes.node,
    onClose: PropTypes.func.isRequired,
  };



  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <React.Fragment>
      <Header loginHandler={loginHandler} />
      
      {
        (localStorage.getItem("username") !== undefined && localStorage.getItem("username") !== null && localStorage.getItem("username").trim() !== "") ? 
        (
          <>
          <br></br><br></br><br></br><br></br><br></br>
          <Grid container direction="row" rowSpacing={2} style={{ paddingLeft: '100px' }} spacing={{ xs: 2, md: 4 }} columns={{ xs: 4, sm: 8, md: 12 }}>
          {
            bands.length > 0 ? ( bands.map((each, i) => (
              
              <Grid item>
                <Card sx={{   display:'flex', minWidth:'87%', maxWidth:'87%' }} >
                  <CardMedia
                  sx={{ padding: "1em 1em 0 1em", objectFit: "contain", minWidth:'15%', maxWidth:'15%'  }}
                  
                    component="img"
                    image={band}
                    
                  />
                  <Box sx={{ display: 'inline-flex', flexDirection: 'column', width:"90%" }}>
                  <CardActions>
                  <CardContent style={{ fontFamily: 'Roboto', width:"100%"}}>
                  <div style={{display:'flex', flexWrap:'wrap'}}>
                    <Grid item md={10}>
                    <Typography gutterBottom variant="h6" component="div" style={{  fontSize: 17, fontWeight: '700', float:'left', fontFamily:'fantasy' }}>
                      {each.bandName}
                    </Typography>
                    </Grid>
                    <Grid item md={2}>
                    <Typography variant="body2" color="text.secondary" style={{display: 'inline-flex', float:'right'}}>
                      <Chip style={{float:'right'}} label={"Price : "+ (each.price) +" "+ (each.currency.code)} color='success'>
                       
                      </Chip>
                      </Typography>
                      </Grid>
                    </div>
                    
                    
                    <Typography variant="body2" color="text.secondary" >
                      Genre : {each.genre.genre}
                    </Typography>
                    <br></br>
                    <div style={{display:'flex', flexWrap:'wrap', float:"left"}}>
                    
                    <PersonRounded/>{each.createdBy.firstName} {each.createdBy.lastName} ({each.createdBy.username})
                    
                    </div>
                    {
        (localStorage.getItem("username") !== undefined && localStorage.getItem("username") !== null && localStorage.getItem("username").trim() !== "" && localStorage.getItem("role")=== "musician" && !bandOwner) ? 
                   
                   (
                    (appliedBandIds.includes(each.id)) ? ( 
                   <div style={{display:'flex', flexWrap:'wrap', float:"right"}}>
                    <Button variant='contained' color='warning' > <BookmarksIcon/> APPLIED ALREADY</Button>
                    </div>
                     ):(
                      <div style={{display:'flex', flexWrap:'wrap', float:"right"}}>
                      <Button variant='contained' onClick={(e) => toggleApplyBandModal(each.id)} > <BookmarksIcon/> APPLY</Button>
                      </div>
                     )
                    
                    ):""
}
                    <br></br>
                    </CardContent>
                    <ExpandMore
                        expand={expanded}
                        onClick={()=>handleExpandClick(i, each.id)}
                        aria-expanded={expanded===i}
                        aria-label="show more"
                        >
                            <IconButton  style={{backgroundColor:  "#001f3d"}}>
                        <ExpandMoreIcon style={{fill: "white"}}/>
                        </IconButton>
                        </ExpandMore>
                        </CardActions>
                        <Collapse in={expanded===i} timeout="auto" unmountOnExit >
                        <Grid container direction={'row'}>
                          <Grid item md={5}>
                          <Card style={{padding:'10px'}}>
                          <CardHeader avatar={<VolumeUpIcon style={{fill:'white'}}/>} title={"Instruments"} titleTypographyProps={{variant:'body1' }} style={{backgroundColor:'#262673', color:'white'}}>
                            </CardHeader>
                            <CardContent>
                            {
                              each.instruments.length > 0 ? (
                                <Box>
                                  {each.instruments.map((ins, ind) => (
                                     
                                    <Typography variant="body2" color="text.secondary" >
                                        {ins.name}
                                    </Typography>

                                      
                                  ))}
                                  </Box>
                               ) : ""
                            }
                            </CardContent>
                          </Card>
                          </Grid>
                          <Grid item md={1}></Grid>
                          <Grid item md={5}>
                          <Card style={{padding:'10px'}}>
                            <CardHeader avatar={<SpatialAudioOffIcon style={{fill:'white'}}/>} title={"Musicians"} titleTypographyProps={{variant:'body1' }} style={{backgroundColor:'#262673', color:'white'}}>
                            </CardHeader>
                            <CardContent>
                            {
                              each.musicians.length > 0 ? (
                                <Box>
                                  {each.musicians.map((ins, ind) => (
                                     
                                     <div style={{display:'flex', flexWrap:'wrap', padding:'5px'}}>
                    
                                     <PersonRounded/>{ins.firstName} {ins.lastName}
                                     
                                     </div>

                                      
                                  ))}
                                  </Box>
                               ) : ""
                            }
                            </CardContent>
                            </Card>
                            </Grid>
                            </Grid>
                            <br></br><br></br>
                    </Collapse>
                    </Box>
                    </Card>
                    </Grid>
            ))
            ):(
              <p style={{color:'red', padding:'10px'}}>No records found!</p>
             )
          }
          </Grid>
          </>
        ) : (
            
          <div  style={{minHeight:'800px', width:'100%', 
          backgroundPosition: 'center',
  backgroundSize: 'cover',
  backgroundRepeat: 'no-repeat',
          
          background: "url(" + "https://s3.amazonaws.com/busites_www/bradpaisley/1-default/2-brad-paisley/hkjv3r_1537466314.jpg" + ") no-repeat center center fixed", width:'100%', height:'100%'}}>
          <br></br><br></br><br></br><br></br>
          <Box style={{padding:"20px", marginTop:"30px", marginLeft:"50px", backgroundColor:"white", color:'black', width:"30%"}}>
          <h1>BANDIFY</h1>
          <br></br>
          <p>Offers 1000+ bands</p>
          <Button variant='outlined' onClick={()=> openBandsPage()}>VIEW BANDS</Button>
          </Box>
          <br></br>
      </div>
            

        )
      }
      


<Snackbar
        style={{ top: '50%', bottom: '50%', left: '40%', right: '50%' ,whiteSpace: 'pre-wrap', width: '300px'}}
        autoHideDuration={4000}
        anchorOrigin={{
          vertical: "center",
          horizontal: "center"
        }}
        
        open={openSnack}
        onClose={handleSnackClose}
      >
<Alert onClose={handleSnackClose} severity={severity} sx={{ width: '100%' }}>
{snackMessage}
  </Alert>

      </Snackbar>     
    </React.Fragment>
  );
}
